cy.get('input[placeholder*="Busca tu próxima"]').as('search');
cy.get('@search').clear().type('Test evento{enter}');
// la URL debe incluir el query
cy.url().should('include', 'search=Test%20evento');
// las tarjetas resultantes contienen el texto
cy.get('.event-card .event-title').each($t => cy.wrap($t).should('contain.text', 'Test evento'));

